import React, { useContext, useEffect, useState } from "react";
import Navbar from "../components/Navbar";

import { BookingContext } from "../Context/BookingsContextProvider";
import { SlotsContext } from "../Context/SlotsContextProvider";

const background =
  "https://media.istockphoto.com/id/1356830579/photo/newly-striped-parking-lot.jpg?s=612x612&w=0&k=20&c=oJtKltmJGjsZ5SVllCVwm3cJzHQafcZmgbmbpWkH3tw=";

const START_DETAILS = {
  name: "",
  carNumber: "",
  phone: 0,
};

function Home() {
  const [booking, setBooking] = useState(false);
  const {slots, setSlots}= useContext(SlotsContext);
  const [details, setDetails] = useState(START_DETAILS);
  const { carbookings, setCarbookings } = useContext(BookingContext);

  const BookSlot = (e) => {
    e.preventDefault();
      if (!details.carNumber || !details.name || !details.phone) {
        alert("please enter the values in all fields");
        return;
      }
      setCarbookings((prev) => {
        return [...prev, details];
      });
      setDetails(START_DETAILS);
      setSlots(slots - 1);
      console.log(carbookings);
  };

  const updateValue = (e) => {
    const field = e.target.id;
    setDetails({ ...details, [field]: e.target.value });
  };

  useEffect(() => {
    console.log(carbookings);
  }, [slots]);

  return (
    <div>
      <div className="h-dvh w-dvw p-12">
        <div className="text-center">
          <div className="text-center border-black border-2 rounded-md">
            Available Slots : {slots}
          </div>
          <button
            onClick={() => {
              setBooking(!booking);
            }}
            className="text-white bg-blue-900 p-2 rounded-md mt-8"
          >
            {booking ? "Cancel" : "Book Slot"}
          </button>
        </div>
        {!booking ? null : slots === 0 ? (
          <div id="warning" className="text-red-700 text-center mt-5">
            Booking Not possible
          </div>
        ) : (
          <form className="text-blue-900 m-auto border-2 w-1/2 p-10">
            <label htmlFor="name">Name :</label>
            <br />
            <input
              onChange={updateValue}
              value={details.name}
              className="text-black"
              id="name"
              type="text"
              required
            />
            <br />
            <label htmlFor="carNumber"> Car Number :</label>
            <br />
            <input
              onChange={updateValue}
              value={details.carNumber}
              className="text-black"
              id="carNumber"
              type="text"
              required
            />
            <br />
            <label htmlFor="phone">Phone Number : </label>
            <br />
            <input
              onChange={updateValue}
              value={details.phone}
              className="text-black"
              id="phone"
              type="tel"
              required
            />
            <br />
            <button
              onClick={BookSlot}
              className="bg-blue-900 text-white rounded-md p-2"
            >
              Book Slot
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Home;
