  return (
    <div>
      <Navbar></Navbar>

      <div className="h-dvh w-dvw p-12">
        <div className="text-center">
          <div className="text-center border-black border-2 rounded-md">
            Available Slots : {slots}
          </div>
          <button
            onClick={() => {
              setBooking(!booking);
            }}
            className="text-white bg-blue-900 p-2 rounded-md mt-8"
          >
            {booking ? "Cancel" : "Book Slot"}
          </button>
        </div>
        {!booking ? null : slots === 0 ? (
          <div id="warning" className="text-red-700 text-center mt-5">
            Booking Not possible
          </div>
        ) : (
          <form className="text-blue-900 m-auto border-2 w-1/2 p-10">
            <label htmlFor="name">Name :</label>
            <br />
            <input className="text-black" id="name" name="name" type="text" />
            <br />
            <label htmlFor="car_number"> Car Number :</label>
            <br />
            <input
              className="text-black"
              id="car_number"
              type="text"
              name="car_number"
            />
            <br />
            <label htmlFor="phone">Phone Number : </label>
            <br />
            <input className="text-black" id="phone" type="tel" name="phone" />
            <br />
            <button
              onClick={BookSlot}
              className="bg-blue-900 text-white rounded-md p-2"
            >
              Book Slot
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Home;
