import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";

const background =
  "https://media.istockphoto.com/id/1356830579/photo/newly-striped-parking-lot.jpg?s=612x612&w=0&k=20&c=oJtKltmJGjsZ5SVllCVwm3cJzHQafcZmgbmbpWkH3tw=";

function Home() {
  const [booking, setBooking] = useState(false);
  const [slots, setSlots] = useState(90);

//   useEffect(() => {
//   }, [booking,slots]);

  const BookSlot = ()=>{
    if(slots>0) setSlots(slots-1);
  }
  return (
    <div>
      <Navbar></Navbar>

      <div className="h-dvh w-dvw p-12">
        <div className="text-center">
          <div className="text-center border-black border-2 rounded-md">
            Available Slots : {slots}
          </div>
          <button onClick={()=>{setBooking(!booking)}} className="text-white bg-blue-900 p-2 rounded-md mt-8">{booking ? "Cancel" : "Book Slot"}</button>
        </div>
        {!booking ? null : (
          <form className="text-blue-900 m-auto border-2 w-1/2 p-10">
            <label htmlFor="name">Name :</label>
            <br />
            <input id="name" name="name" type="text" /> <br />
            <label htmlFor="car_number"> Car Number :</label>
            <br />
            <input id="car_number" type="text" name="car_number" /> <br />
            <label htmlFor="phone">Phone Number : </label>
            <br />
            <input id="phone" type="tele" name="phone" /> <br />
            <button onClick={BookSlot} className="bg-blue-900 text-white rounded-md p-2">
              Book Slot
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Home;
