import React, { useContext, useState } from "react";
import { auth } from "../firebase";
import { signInWithEmailAndPassword } from "firebase/auth";
import { UserContext } from "../Context/UserContextProvider";

const START_STATE = {
  email: "",
  password: "",
};

function Login() {
  const [details, setDetails] = useState(START_STATE);

  const {user,setUser} = useContext(UserContext);

  const userlogin = (e) => {
    e.preventDefault();
    signInWithEmailAndPassword(auth, details.email, details.password)
      .then((userCredentials) => {
        setUser(userCredentials.user);
      })
      .catch((error) => {
        setUser("");
      });
  };

  useEffect(()=>{
    console.log(user)
  },[user]);

  const updateValue = (e) => {
    const field = e.target.id;
    setDetails((prev) => {
      return { ...prev, [field]: e.target.value };
    });
    console.log(details);
  };

  return (
    <>
      <div className="flex items-center justify-center flex-col bg-blue-900 w-3/4 rounded-xl h-64 text-white mx-auto mt-24 p-12">
        <h1 className="text-3xl">User Login</h1>
        <form className="text-center">
          <div className="my-2">
            <label htmlFor="email">Username/Email : </label>
            <input
              className="rounded-md text-blue-500"
              onChange={updateValue}
              id="email"
              type="email"
              value={details.email}
              required
            />
            <br />
          </div>
          <div className="my-2">
            <label htmlFor="password">Password: </label>
            <input
              className="rounded-md text-blue-500"
              onChange={updateValue}
              id="password"
              type="password"
              value={details.password}
              required
            />
            <br />
          </div>
          <div className="text-center ">
            <button
              onClick={userlogin}
              className="bg-white px-2 rounded-md text-blue-900"
            >
              Login
            </button>
          </div>
        </form>
      </div>
    </>
  );
}

export default Login;
