import React, { useState } from 'react'

export const SlotsContext = React.createContext();
function SlotsContextProvider(props) {
    const [slots, setSlots] = useState(90);
  return (
    <>
    <SlotsContext.Provider value ={{slots,setSlots}}>
    {props.children}
    </SlotsContext.Provider>
    </>
  )
}

export default SlotsContextProvider
